<?phpnamespace Drupal\seo_site\Form;use Drupal\Core\Form\ConfigFormBase;use Drupal\Core\Form\FormStateInterface;class SeoSiteForm extends ConfigFormBase {  /**   * {@inheritDoc}   */  protected function getEditableConfigNames() {    return ['seo_site.site_configuration'];  }  /**   * {@inheritDoc}   */  public function getFormId() {    return 'seo_site_form';  }  /**   * {@inheritDoc}   */  public function buildForm(array $form, FormStateInterface $form_state) {    $form['tabs'] = [      '#type' => 'horizontal_tabs',    ];    $form['base_settings'] = [      '#type' => 'details',      '#title' => '基础设置',      '#group' => 'tabs',    ];//    $form['other_settings'] = [//      '#type' => 'details',//      '#title' => '其他设置',//      '#group' => 'tabs',//    ];    $form['base_settings']['insert_top'] = [      '#type' => 'radios',      '#title' => '页面顶部插入代码',      '#options' => [1 => '是', 0 => '否'],      '#default_value' => $this->config('seo_site.site_configuration')->get('insert_top') ?: 0,    ];    $form['base_settings']['insert_top_code'] = [      '#type' => 'text_format',      '#format' => 'plain_text',      '#title' => '顶部插入的代码',      '#default_value' => $this->config('seo_site.site_configuration')->get('insert_top_code'),    ];    return parent::buildForm($form, $form_state);  }  /**   * {@inheritDoc}   */  public function submitForm(array &$form, FormStateInterface $form_state) {    $this->config('seo_site.site_configuration')      ->set('insert_top', $form_state->getValue('insert_top'))      ->set('insert_top_code', $form_state->getValue('insert_top_code'))      ->save();    parent::submitForm($form, $form_state);  }}