<?phpnamespace Drupal\seo_textdata\Controller;use Drupal\Core\Controller\ControllerBase;use Drupal\seo_textdata\Entity\TextDataInterface;use Symfony\Component\HttpFoundation\BinaryFileResponse;use Symfony\Component\HttpFoundation\Response;class TextdataController extends ControllerBase {  /**   * seo_textdata_type label.   */  public function getTitle() {    $seo_textdata_type = \Drupal::routeMatch()->getParameter('seo_textdata_type');    $type = \Drupal::entityTypeManager()->getStorage('seo_textdata_type')->load($seo_textdata_type);    if (empty($type)) {      return '';    }    return $type->label();  }  public function downloadFile(TextDataInterface $seo_textdata = NULL) {    /** @var \Drupal\file\FileInterface $file */    $file = $seo_textdata->attachment->entity;    $default_headers = array(      'Content-Type' => 'force-download',      'Content-Disposition' => 'attachment; filename="' . $file->getFilename() . '"',      'Content-Length' => $file->getSize(),      'Content-Transfer-Encoding' => 'binary',      'Pragma' => 'no-cache',      'Cache-Control' => 'must-revalidate, post-check=0, pre-check=0',      'Expires' => '0',      'Accept-Ranges' => 'bytes'    );    return new BinaryFileResponse($file->getFileUri(), 200, $default_headers);  }}