<?phpnamespace Drupal\seo_textdata\Form;use Drupal\Core\Form\FormBase;use Drupal\Core\Form\FormStateInterface;class TextdataFilePreview extends FormBase {  public function getFormId() {    return 'seo_textdata_file_preview';  }  public function buildForm(array $form, FormStateInterface $form_state, $seo_textdata = NULL) {    if (empty($seo_textdata)) {      return $form;    }    $uri = $seo_textdata->attachment->entity->getFileUri();    $form['file'] = [      '#type' => 'item',      '#title' => '文件',      '#markup' => file_create_url($uri),    ];    $form['text'] = [      '#type' => 'textarea',      '#title' => '内容',      '#default_value' => seo_textdata_auto_read($uri),      '#disabled' => TRUE,      '#rows' => 20,    ];    return $form;  }  public function submitForm(array &$form, FormStateInterface $form_state) {    // TODO: Implement submitForm() method.  }}