<?phpnamespace Drupal\seo_textdata\Menu;use Drupal\Core\Menu\LocalTaskDefault;use Symfony\Component\HttpFoundation\Request;class LocalTask extends LocalTaskDefault {  /**   * {@inheritDoc}   */  public function getTitle(Request $request = NULL) {    $seo_textdata_type = \Drupal::routeMatch()->getParameter('seo_textdata_type');    $type = \Drupal::entityTypeManager()->getStorage('seo_textdata_type')->load($seo_textdata_type);    if (empty($type)) {      return '';    }    return $type->label();  }}