<?phpnamespace Drupal\seo_textdata\Menu;use Drupal\Core\Menu\LocalTaskDefault;use Drupal\Core\Routing\RouteMatchInterface;use Drupal\Core\Url;use Symfony\Component\HttpFoundation\Request;class LocalTaskTextData extends LocalTaskDefault {  /**   * {@inheritDoc}   */  public function getTitle(Request $request = NULL) {    $seo_textdata_type = \Drupal::routeMatch()->getParameter('seo_textdata_type');    $type = \Drupal::entityTypeManager()->getStorage('seo_textdata_type')->load($seo_textdata_type);    if (empty($type)) {      return '';    }    if ($this->getPluginId() == 'seo_textdata.import') {      return '上传' . $type->label();    }    return $type->label();  }  public function getOptions(RouteMatchInterface $route_match) {    $options = parent::getOptions($route_match);    if ($this->getPluginId() == 'seo_textdata.import') {      $route_match = \Drupal::routeMatch();      $options['attributes']['class'] = ['use-ajax'];      $options['attributes']['data-dialog-type'] = 'modal';      $options['attributes']['data-dialog-options'] = '{"width": "80%"}';      $route_parameter = $route_match->getParameter('seo_textdata_type');      $route_name = $route_match->getRouteName();      $options['query']['destination'] = Url::fromRoute($route_name, ['seo_textdata_type' => $route_parameter])->toString();    }    return $options;  }}